import React, { useContext, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { apiCalls } from '../services/api';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { cart, clearCart, getTotalPrice } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('upi');
  const [upiId, setUpiId] = useState('');
  const [error, setError] = useState('');
  const [showSuccess, setShowSuccess] = useState(false);

  const orderData = location.state?.orderData;
  const totalAmount = getTotalPrice();
  const tax = totalAmount * 0.05;
  const finalTotal = totalAmount + tax;

  if (!orderData || !cart.length) {
    return (
      <div className="payment-page">
        <p className="error">No order data. Please go back and try again.</p>
      </div>
    );
  }

  const handlePayment = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Validate UPI ID if UPI method selected
      if (paymentMethod === 'upi') {
        if (!upiId.trim()) {
          setError('Please enter your UPI ID');
          setLoading(false);
          return;
        }
        if (!upiId.includes('@')) {
          setError('Please enter a valid UPI ID (e.g., yourname@upi)');
          setLoading(false);
          return;
        }
      }

      // Create order first
      const orderResponse = await apiCalls.createOrder({
        ...orderData,
        items: cart,
        totalPrice: finalTotal,
        paymentMethod,
      });

      const orderId = orderResponse.data.order._id;

      // Process payment
      const paymentResponse = await apiCalls.processPayment({
        orderId,
        amount: finalTotal,
        paymentMethod,
        upiId: paymentMethod === 'upi' ? upiId : null,
      });

      if (!paymentResponse.data.success) {
        setError(paymentResponse.data.message || 'Payment failed');
        setLoading(false);
        return;
      }

      // Payment successful
      clearCart();
      setShowSuccess(true);

      setTimeout(() => {
        navigate('/orders');
      }, 3000);
    } catch (err) {
      setError(
        err.response?.data?.message || 'Failed to process payment. Please try again.'
      );
    }
    setLoading(false);
  };

  if (showSuccess) {
    return (
      <div className="payment-page">
        <div className="success-message">
          <div className="success-icon">âœ“</div>
          <h2>Payment Successful!</h2>
          <p>Your order has been placed successfully.</p>
          <p>Redirecting to orders...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="payment-page">
      <h1>Payment</h1>
      <div className="payment-container">
        <div className="payment-form-section">
          <h2>Choose Payment Method</h2>
          <form onSubmit={handlePayment} className="payment-form">
            {error && <p className="error">{error}</p>}

            <div className="payment-methods">
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="upi"
                  checked={paymentMethod === 'upi'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <span>UPI Payment</span>
              </label>
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="cod"
                  checked={paymentMethod === 'cod'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <span>Cash on Delivery</span>
              </label>
            </div>

            {paymentMethod === 'upi' && (
              <div className="upi-details">
                <h3>UPI Payment</h3>
                <input
                  type="text"
                  placeholder="Enter UPI ID (example@upi)"
                  value={upiId}
                  onChange={(e) => setUpiId(e.target.value)}
                  required
                />
                <p className="info-text">
                  âœ“ Safe and secure payment
                  <br />
                  âœ“ Instant confirmation
                  <br />
                  âœ“ Get order tracking
                </p>
              </div>
            )}

            {paymentMethod === 'cod' && (
              <div className="cod-details">
                <h3>Cash on Delivery</h3>
                <p className="info-text">
                  ðŸ’µ Pay â‚¹{finalTotal.toFixed(2)} when your order arrives
                  <br />
                  âœ“ No prepayment required
                  <br />
                  âœ“ Verify order before payment
                </p>
              </div>
            )}

            <button type="submit" disabled={loading} className="pay-button">
              {loading
                ? 'Processing Payment...'
                : paymentMethod === 'upi'
                ? `Pay â‚¹${finalTotal.toFixed(2)} via UPI`
                : `Place Order - Pay on Delivery`}
            </button>
          </form>
        </div>

        <div className="order-summary-section">
          <h2>Order Summary</h2>
          <div className="summary-items">
            {cart.map((item) => (
              <div key={item._id} className="summary-item">
                <span>{item.name} x {item.quantity}</span>
                <span>â‚¹{(item.price * item.quantity).toFixed(2)}</span>
              </div>
            ))}
          </div>
          <div className="summary-totals">
            <div className="summary-row">
              <span>Subtotal:</span>
              <span>â‚¹{totalAmount.toFixed(2)}</span>
            </div>
            <div className="summary-row">
              <span>Tax (5%):</span>
              <span>â‚¹{tax.toFixed(2)}</span>
            </div>
            <div className="summary-row total">
              <span>Total:</span>
              <span>â‚¹{finalTotal.toFixed(2)}</span>
            </div>
          </div>
          <div className="delivery-info">
            <h4>Delivery Details</h4>
            <p>
              <strong>Address:</strong> {orderData.address}
            </p>
            <p>
              <strong>Phone:</strong> {orderData.phone}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
