import React, { useContext, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { apiCalls } from '../services/api';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { cart, clearCart, getTotalPrice } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [cardDetails, setCardDetails] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardholderName: '',
  });

  const orderData = location.state?.orderData;
  const totalAmount = getTotalPrice();
  const tax = totalAmount * 0.05;
  const finalTotal = totalAmount + tax;

  if (!orderData || !cart.length) {
    return (
      <div className="payment-page">
        <p className="error">No order data. Please go back and try again.</p>
      </div>
    );
  }

  const handleCardInputChange = (e) => {
    const { name, value } = e.target;
    setCardDetails((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const formatCardNumber = (value) => {
    return value.replace(/\s/g, '').replace(/(\d{4})(?=\d)/g, '$1 ');
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Validate card details
      if (paymentMethod === 'card') {
        if (!cardDetails.cardNumber || !cardDetails.expiryDate || !cardDetails.cvv) {
          alert('Please fill all card details');
          setLoading(false);
          return;
        }
      }

      // Create order with payment info
      const completeOrderData = {
        ...orderData,
        items: cart,
        totalPrice: finalTotal,
        paymentMethod,
        paymentStatus: 'completed',
      };

      await apiCalls.createOrder(completeOrderData);
      clearCart();
      alert('Order placed successfully!');
      navigate('/orders');
    } catch (err) {
      alert('Payment failed. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="payment-page">
      <h1>Payment</h1>
      <div className="payment-container">
        <div className="payment-form-section">
          <h2>Choose Payment Method</h2>
          <form onSubmit={handlePayment} className="payment-form">
            <div className="payment-methods">
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="card"
                  checked={paymentMethod === 'card'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <span>Debit/Credit Card</span>
              </label>
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="upi"
                  checked={paymentMethod === 'upi'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <span>UPI</span>
              </label>
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="wallet"
                  checked={paymentMethod === 'wallet'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <span>Digital Wallet</span>
              </label>
              <label className="payment-option">
                <input
                  type="radio"
                  name="paymentMethod"
                  value="cod"
                  checked={paymentMethod === 'cod'}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                />
                <span>Cash on Delivery</span>
              </label>
            </div>

            {paymentMethod === 'card' && (
              <div className="card-details">
                <h3>Card Details</h3>
                <input
                  type="text"
                  name="cardholderName"
                  placeholder="Cardholder Name"
                  value={cardDetails.cardholderName}
                  onChange={handleCardInputChange}
                  required
                />
                <input
                  type="text"
                  name="cardNumber"
                  placeholder="Card Number (1234 5678 9012 3456)"
                  value={formatCardNumber(cardDetails.cardNumber)}
                  onChange={(e) =>
                    setCardDetails((prev) => ({
                      ...prev,
                      cardNumber: e.target.value.replace(/\s/g, ''),
                    }))
                  }
                  maxLength="19"
                  required
                />
                <div className="card-row">
                  <input
                    type="text"
                    name="expiryDate"
                    placeholder="MM/YY"
                    value={cardDetails.expiryDate}
                    onChange={handleCardInputChange}
                    maxLength="5"
                    required
                  />
                  <input
                    type="password"
                    name="cvv"
                    placeholder="CVV"
                    value={cardDetails.cvv}
                    onChange={handleCardInputChange}
                    maxLength="3"
                    required
                  />
                </div>
              </div>
            )}

            {paymentMethod === 'upi' && (
              <div className="upi-details">
                <h3>UPI Payment</h3>
                <input
                  type="email"
                  placeholder="Enter UPI ID (example@upi)"
                  required
                />
                <p className="info-text">You will be redirected to your UPI app</p>
              </div>
            )}

            {paymentMethod === 'cod' && (
              <div className="cod-details">
                <h3>Cash on Delivery</h3>
                <p className="info-text">
                  You can pay ₹{finalTotal.toFixed(2)} when your order arrives
                </p>
              </div>
            )}

            <button type="submit" disabled={loading} className="pay-button">
              {loading ? 'Processing...' : `Pay ₹${finalTotal.toFixed(2)}`}
            </button>
          </form>
        </div>

        <div className="order-summary-section">
          <h2>Order Summary</h2>
          <div className="summary-items">
            {cart.map((item) => (
              <div key={item._id} className="summary-item">
                <span>{item.name} x {item.quantity}</span>
                <span>₹{(item.price * item.quantity).toFixed(2)}</span>
              </div>
            ))}
          </div>
          <div className="summary-totals">
            <div className="summary-row">
              <span>Subtotal:</span>
              <span>₹{totalAmount.toFixed(2)}</span>
            </div>
            <div className="summary-row">
              <span>Tax (5%):</span>
              <span>₹{tax.toFixed(2)}</span>
            </div>
            <div className="summary-row total">
              <span>Total:</span>
              <span>₹{finalTotal.toFixed(2)}</span>
            </div>
          </div>
          <div className="delivery-info">
            <h4>Delivery Details</h4>
            <p>
              <strong>Address:</strong> {orderData.address}
            </p>
            <p>
              <strong>Phone:</strong> {orderData.phone}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
