import React, { useState, useEffect } from 'react';
import { apiCalls } from '../services/api';
import './AdminPage.css';

const AdminPage = () => {
  const [activeTab, setActiveTab] = useState('inventory');
  const [menuItems, setMenuItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [editingItem, setEditingItem] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: 'pizzas',
    image: '',
  });

  useEffect(() => {
    if (activeTab === 'inventory') {
      fetchMenuItems();
    } else if (activeTab === 'orders') {
      fetchAllOrders();
    }
  }, [activeTab]);

  const fetchMenuItems = async () => {
    setLoading(true);
    try {
      const response = await apiCalls.getMenu();
      setMenuItems(response.data);
      setError('');
    } catch (err) {
      setError('Failed to load menu items');
    }
    setLoading(false);
  };

  const fetchAllOrders = async () => {
    setLoading(true);
    try {
      const response = await apiCalls.getAllOrders();
      setOrders(response.data);
      setError('');
    } catch (err) {
      setError('Failed to load orders');
    }
    setLoading(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (editingItem) {
        await apiCalls.updateMenuItem(editingItem._id, formData);
        alert('Item updated successfully');
      } else {
        await apiCalls.addMenu(formData);
        alert('Item added successfully');
      }

      setFormData({
        name: '',
        description: '',
        price: '',
        category: 'pizzas',
        image: '',
      });
      setEditingItem(null);
      setShowForm(false);
      fetchMenuItems();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save item');
    }
    setLoading(false);
  };

  const handleEditItem = (item) => {
    setEditingItem(item);
    setFormData({
      name: item.name,
      description: item.description,
      price: item.price,
      category: item.category,
      image: item.image,
    });
    setShowForm(true);
  };

  const handleDeleteItem = async (itemId) => {
    if (window.confirm('Delete this item?')) {
      setLoading(true);
      try {
        await apiCalls.deleteMenuItem(itemId);
        alert('Item deleted');
        fetchMenuItems();
      } catch (err) {
        setError('Failed to delete item');
      }
      setLoading(false);
    }
  };

  const handleCancelEdit = () => {
    setEditingItem(null);
    setFormData({
      name: '',
      description: '',
      price: '',
      category: 'pizzas',
      image: '',
    });
    setShowForm(false);
  };

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h1>Admin Dashboard</h1>
      </div>

      <div className="admin-tabs">
        <button
          className={`tab-button ${activeTab === 'inventory' ? 'active' : ''}`}
          onClick={() => setActiveTab('inventory')}
        >
          ðŸ“¦ Inventory
        </button>
        <button
          className={`tab-button ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => setActiveTab('orders')}
        >
          ðŸ“‹ Orders
        </button>
      </div>

      {error && <p className="error">{error}</p>}

      {/* Inventory Tab */}
      {activeTab === 'inventory' && (
        <div className="inventory-section">
          <div className="inventory-header">
            <h2>Menu Items</h2>
            {!showForm && (
              <button onClick={() => setShowForm(true)} className="add-item-btn">
                âž• Add Item
              </button>
            )}
          </div>

          {showForm && (
            <div className="item-form-container">
              <h3>{editingItem ? 'Edit Item' : 'Add Item'}</h3>
              <form onSubmit={handleAddItem} className="item-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <textarea
                  name="description"
                  placeholder="Description"
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="number"
                  name="price"
                  placeholder="Price"
                  value={formData.price}
                  onChange={handleInputChange}
                  required
                />
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                >
                  <option value="pizzas">Pizzas</option>
                  <option value="drinks">Drinks</option>
                  <option value="breads">Breads</option>
                  <option value="desserts">Desserts</option>
                </select>
                <input
                  type="url"
                  name="image"
                  placeholder="Image URL"
                  value={formData.image}
                  onChange={handleInputChange}
                  required
                />
                <div className="form-buttons">
                  <button type="submit" disabled={loading} className="submit-btn">
                    {loading ? 'Saving...' : 'Save'}
                  </button>
                  <button type="button" onClick={handleCancelEdit} className="cancel-btn">
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          )}

          {loading && !showForm ? (
            <p>Loading...</p>
          ) : (
            <div className="items-table">
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Category</th>
                    <th>Price</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {menuItems.map((item) => (
                    <tr key={item._id}>
                      <td>{item.name}</td>
                      <td>{item.category}</td>
                      <td>â‚¹{item.price}</td>
                      <td>
                        <button onClick={() => handleEditItem(item)} className="edit-btn">
                          Edit
                        </button>
                        <button onClick={() => handleDeleteItem(item._id)} className="delete-btn">
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {/* Orders Tab */}
      {activeTab === 'orders' && (
        <div className="orders-section">
          <h2>All Orders</h2>
          {loading ? (
            <p>Loading...</p>
          ) : orders.length === 0 ? (
            <p>No orders</p>
          ) : (
            <div className="orders-list">
              {orders.map((order) => (
                <div key={order._id} className="order-card">
                  <p><strong>Order #{order._id.slice(-6)}</strong></p>
                  <p>Customer: {order.userId?.name || 'Unknown'}</p>
                  <p>Phone: {order.phone}</p>
                  <p>Total: â‚¹{order.totalPrice}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AdminPage;
