import React, { useState, useEffect } from 'react';
import { apiCalls } from '../services/api';
import './AdminPage.css';

const AdminPage = () => {
  const [activeTab, setActiveTab] = useState('inventory');
  const [menuItems, setMenuItems] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [editingItem, setEditingItem] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: 'pizzas',
    image: '',
  });

  useEffect(() => {
    if (activeTab === 'inventory') {
      fetchMenuItems();
    } else if (activeTab === 'orders') {
      fetchAllOrders();
    }
  }, [activeTab]);

  const fetchMenuItems = async () => {
    setLoading(true);
    try {
      const response = await apiCalls.getMenu();
      setMenuItems(response.data);
      setError('');
    } catch (err) {
      setError('Failed to load menu items');
    }
    setLoading(false);
  };

  const fetchAllOrders = async () => {
    setLoading(true);
    try {
      const response = await apiCalls.getAllOrders();
      setOrders(response.data);
      setError('');
    } catch (err) {
      setError('Failed to load orders');
    }
    setLoading(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (editingItem) {
        // Update existing item
        await apiCalls.updateMenuItem(editingItem._id, formData);
        setError('');
        alert('Item updated successfully');
      } else {
        // Add new item
        await apiCalls.addMenu(formData);
        setError('');
        alert('Item added successfully');
      }

      setFormData({
        name: '',
        description: '',
        price: '',
        category: 'pizzas',
        image: '',
      });
      setEditingItem(null);
      setShowForm(false);
      fetchMenuItems();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save item');
    }
    setLoading(false);
  };

  const handleEditItem = (item) => {
    setEditingItem(item);
    setFormData({
      name: item.name,
      description: item.description,
      price: item.price,
      category: item.category,
      image: item.image,
    });
    setShowForm(true);
  };

  const handleDeleteItem = async (itemId) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      setLoading(true);
      try {
        await apiCalls.deleteMenuItem(itemId);
        alert('Item deleted successfully');
        fetchMenuItems();
      } catch (err) {
        setError('Failed to delete item');
      }
      setLoading(false);
    }
  };

  const handleCancelEdit = () => {
    setEditingItem(null);
    setFormData({
      name: '',
      description: '',
      price: '',
      category: 'pizzas',
      image: '',
    });
    setShowForm(false);
  };

  return (
    <div className="admin-page">
      <h1>Admin Panel</h1>
      <div className="admin-tabs">
        <button
          className={`tab-button ${activeTab === 'inventory' ? 'active' : ''}`}
          onClick={() => setActiveTab('inventory')}
        >
          üì¶ Inventory Management
        </button>
        <button
          className={`tab-button ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => setActiveTab('orders')}
        >
          üìã Orders
        </button>
      </div>

      {error && <p className="error">{error}</p>}

      {/* Inventory Management Tab */}
      {activeTab === 'inventory' && (
        <div className="inventory-section">
          <div className="inventory-header">
            <h2>Menu Items</h2>
            {!showForm && (
              <button
                onClick={() => setShowForm(true)}
                className="add-item-btn"
              >
                ‚ûï Add New Item
              </button>
            )}
          </div>

          {showForm && (
            <div className="item-form-container">
              <h3>{editingItem ? 'Edit Item' : 'Add New Item'}</h3>
              <form onSubmit={handleAddItem} className="item-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Item Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <textarea
                  name="description"
                  placeholder="Description"
                  value={formData.description}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="number"
                  name="price"
                  placeholder="Price (‚Çπ)"
                  value={formData.price}
                  onChange={handleInputChange}
                  required
                />
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                >
                  <option value="pizzas">üçï Pizzas</option>
                  <option value="drinks">ü•§ Drinks</option>
                  <option value="breads">üçû Breads</option>
                  <option value="desserts">üç∞ Desserts</option>
                </select>
                <input
                  type="url"
                  name="image"
                  placeholder="Image URL"
                  value={formData.image}
                  onChange={handleInputChange}
                  required
                />
                <div className="form-buttons">
                  <button
                    type="submit"
                    disabled={loading}
                    className="submit-btn"
                  >
                    {loading ? 'Saving...' : editingItem ? 'Update Item' : 'Add Item'}
                  </button>
                  <button
                    type="button"
                    onClick={handleCancelEdit}
                    className="cancel-btn"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          )}

          {loading && !showForm ? (
            <p className="loading">Loading items...</p>
          ) : (
            <div className="items-table">
              <table>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Category</th>
                    <th>Price</th>
                    <th>Description</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {menuItems.length === 0 ? (
                    <tr>
                      <td colSpan="5" className="no-data">
                        No items found
                      </td>
                    </tr>
                  ) : (
                    menuItems.map((item) => (
                      <tr key={item._id}>
                        <td className="item-name">{item.name}</td>
                        <td>
                          <span className="category-badge">
                            {item.category}
                          </span>
                        </td>
                        <td className="price">‚Çπ{item.price}</td>
                        <td className="description">
                          {item.description.substring(0, 30)}...
                        </td>
                        <td className="actions">
                          <button
                            onClick={() => handleEditItem(item)}
                            className="edit-btn"
                          >
                            ‚úèÔ∏è Edit
                          </button>
                          <button
                            onClick={() => handleDeleteItem(item._id)}
                            className="delete-btn"
                          >
                            üóëÔ∏è Delete
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}

      {/* Orders Tab */}
      {activeTab === 'orders' && (
        <div className="orders-section">
          <h2>All Orders</h2>
          {loading ? (
            <p className="loading">Loading orders...</p>
          ) : orders.length === 0 ? (
            <p className="no-data">No orders found</p>
          ) : (
            <div className="orders-list">
              {orders.map((order) => (
                <div key={order._id} className="order-card">
                  <div className="order-header-info">
                    <div className="order-id">
                      <strong>Order #{order._id.slice(-6).toUpperCase()}</strong>
                      <span className="order-date">
                        {new Date(order.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                    <div className="order-customer">
                      <p><strong>Customer:</strong> {order.userId?.name || 'Unknown'}</p>
                      <p><strong>Phone:</strong> {order.phone}</p>
                    </div>
                  </div>

                  <div className="order-items-list">
                    <strong>Items:</strong>
                    {order.items.map((item, idx) => (
                      <div key={idx} className="order-item">
                        {item.name} x {item.quantity} - ‚Çπ
                        {(item.price * item.quantity).toFixed(2)}
                      </div>
                    ))}
                  </div>

                  <div className="order-details-info">
                    <p><strong>Delivery Address:</strong> {order.address}</p>
                    <p><strong>Total Amount:</strong> ‚Çπ{order.totalPrice.toFixed(2)}</p>
                    <p><strong>Status:</strong> <span className="status-badge">Order Placed</span></p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AdminPage;
