import React, { useState, useEffect } from 'react';
import { apiCalls } from '../services/api';

const InventoryPage = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [editData, setEditData] = useState({});
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    price: '',
    category: 'pizzas',
    image: '',
    quantity: '',
  });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const response = await apiCalls.getMenu();
      setItems(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to load inventory');
      setLoading(false);
    }
  };

  const handleEditClick = (item) => {
    setEditingId(item._id);
    setEditData({
      price: item.price,
      quantity: item.quantity || 0,
    });
  };

  const handleSaveEdit = async (itemId) => {
    try {
      await apiCalls.updateMenuItem(itemId, editData);
      setItems(
        items.map((item) =>
          item._id === itemId
            ? { ...item, price: editData.price, quantity: editData.quantity }
            : item
        )
      );
      setEditingId(null);
      alert('Item updated successfully');
    } catch (err) {
      alert('Failed to update item');
    }
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    try {
      const response = await apiCalls.addMenu(newItem);
      setItems([...items, response.data.item]);
      setNewItem({
        name: '',
        description: '',
        price: '',
        category: 'pizzas',
        image: '',
        quantity: '',
      });
      setShowAddForm(false);
      alert('Item added successfully');
    } catch (err) {
      alert('Failed to add item');
    }
  };

  const handleDeleteItem = async (itemId) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        await apiCalls.deleteMenuItem(itemId);
        setItems(items.filter((item) => item._id !== itemId));
        alert('Item deleted successfully');
      } catch (err) {
        alert('Failed to delete item');
      }
    }
  };

  if (loading) return <div className="loading">Loading inventory...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="inventory-page">
      <div className="inventory-header">
        <h1>Inventory Management</h1>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          className="add-item-btn"
        >
          {showAddForm ? '✕ Cancel' : '+ Add Item'}
        </button>
      </div>

      {showAddForm && (
        <div className="add-item-form">
          <h3>Add New Item</h3>
          <form onSubmit={handleAddItem}>
            <input
              type="text"
              placeholder="Item Name"
              value={newItem.name}
              onChange={(e) =>
                setNewItem({ ...newItem, name: e.target.value })
              }
              required
            />
            <textarea
              placeholder="Description"
              value={newItem.description}
              onChange={(e) =>
                setNewItem({ ...newItem, description: e.target.value })
              }
              required
            />
            <input
              type="number"
              placeholder="Price"
              value={newItem.price}
              onChange={(e) =>
                setNewItem({ ...newItem, price: parseFloat(e.target.value) })
              }
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={newItem.quantity}
              onChange={(e) =>
                setNewItem({ ...newItem, quantity: parseInt(e.target.value) })
              }
              required
            />
            <select
              value={newItem.category}
              onChange={(e) =>
                setNewItem({ ...newItem, category: e.target.value })
              }
            >
              <option value="pizzas">Pizzas</option>
              <option value="drinks">Drinks</option>
              <option value="breads">Breads</option>
              <option value="desserts">Desserts</option>
            </select>
            <input
              type="url"
              placeholder="Image URL"
              value={newItem.image}
              onChange={(e) =>
                setNewItem({ ...newItem, image: e.target.value })
              }
              required
            />
            <button type="submit" className="save-btn">
              Add Item
            </button>
          </form>
        </div>
      )}

      <div className="inventory-table">
        <table>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Category</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr key={item._id}>
                <td>{item.name}</td>
                <td>{item.category}</td>
                <td>
                  {editingId === item._id ? (
                    <input
                      type="number"
                      value={editData.price}
                      onChange={(e) =>
                        setEditData({
                          ...editData,
                          price: parseFloat(e.target.value),
                        })
                      }
                      className="edit-input"
                    />
                  ) : (
                    `₹${item.price.toFixed(2)}`
                  )}
                </td>
                <td>
                  {editingId === item._id ? (
                    <input
                      type="number"
                      value={editData.quantity}
                      onChange={(e) =>
                        setEditData({
                          ...editData,
                          quantity: parseInt(e.target.value),
                        })
                      }
                      className="edit-input"
                    />
                  ) : (
                    item.quantity || 0
                  )}
                </td>
                <td className="action-buttons">
                  {editingId === item._id ? (
                    <>
                      <button
                        onClick={() => handleSaveEdit(item._id)}
                        className="save-btn"
                      >
                        Save
                      </button>
                      <button
                        onClick={() => setEditingId(null)}
                        className="cancel-btn"
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        onClick={() => handleEditClick(item)}
                        className="edit-btn"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteItem(item._id)}
                        className="delete-btn"
                      >
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default InventoryPage;
