import React, { useContext, useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import QRCodeStyling from 'qr-code-styling';
import { CartContext } from '../context/CartContext';
import { apiCalls } from '../services/api';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { cart, clearCart, getTotalPrice } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showSuccess, setShowSuccess] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('qr');
  const [orderId, setOrderId] = useState(null);
  const qrRef = useRef();

  const orderData = location.state?.orderData;
  const totalAmount = getTotalPrice();
  const tax = totalAmount * 0.05;
  const finalTotal = totalAmount + tax;

  useEffect(() => {
    // Load Google Pay script
    const script = document.createElement('script');
    script.src = 'https://pay.google.com/gp/p/js/pay.js';
    script.async = true;
    document.body.appendChild(script);

    return () => {
      if (document.body.contains(script)) {
        document.body.removeChild(script);
      }
    };
  }, []);

  useEffect(() => {
    if (orderId && qrRef.current) {
      const qrCode = new QRCodeStyling({
        width: 256,
        height: 256,
        data: `upi://pay?pa=pizzahub@googlepay&pn=PizzaHub&am=${finalTotal}&tn=PizzaHub+Order&tr=${orderId}`,
        image:
          'https://upload.wikimedia.org/wikipedia/commons/thumb/2/2f/Google_2015_logo.svg/1200px-Google_2015_logo.svg.png',
        margin: 10,
        qrOptions: {
          typeNumber: 0,
          mode: 'Byte',
          errorCorrectionLevel: 'H',
        },
        imageOptions: {
          hideBackgroundDots: true,
          imageSize: 0.1,
          margin: 0,
        },
        dotsOptions: {
          color: '#222222',
          type: 'rounded',
        },
        backgroundOptions: {
          color: '#ffffff',
        },
      });
      qrCode.append(qrRef.current);
    }
  }, [orderId, finalTotal]);

  if (!orderData || !cart.length) {
    return (
      <div className="payment-page">
        <p className="error">No order data. Please go back and try again.</p>
      </div>
    );
  }

  const generateQRCode = async () => {
    setError('');
    setLoading(true);

    try {
      // Create order first
      const orderResponse = await apiCalls.createOrder({
        ...orderData,
        items: cart,
        totalPrice: finalTotal,
        paymentMethod: 'googlepay',
      });

      const newOrderId = orderResponse.data.order._id;
      setOrderId(newOrderId);
      setPaymentMethod('qr');
    } catch (err) {
      setError('Failed to create order. Please try again.');
    }
    setLoading(false);
  };

  const handleQRScanned = async () => {
    setError('');
    setLoading(true);

    try {
      // Simulate QR code scan - in real scenario, this would come from camera
      // Generate a mock token for testing
      const mockToken = `googlepay_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      // Process payment
      const paymentResponse = await apiCalls.processPayment({
        orderId,
        amount: finalTotal,
        paymentMethod: 'googlepay',
        token: mockToken,
      });

      if (!paymentResponse.data.success) {
        setError(paymentResponse.data.message || 'Payment failed');
        setLoading(false);
        return;
      }

      // Payment successful
      clearCart();
      setShowSuccess(true);

      setTimeout(() => {
        navigate('/orders');
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to process payment');
    }
    setLoading(false);
  };

  const handleDirectGooglePay = async () => {
    if (!window.google || !window.google.payments) {
      setError('Google Pay is not available. Please refresh the page.');
      return;
    }

    setError('');
    setLoading(true);

    try {
      const paymentsClient = new window.google.payments.api.PaymentsClient({
        environment: 'TEST',
      });

      const paymentDataRequest = {
        apiVersion: 2,
        apiVersionMinor: 0,
        allowedPaymentMethods: [
          {
            type: 'CARD',
            parameters: {
              allowedAuthMethods: ['PAN_ONLY', 'CRYPTOGRAM_3DS'],
              allowedCardNetworks: ['MASTERCARD', 'VISA'],
            },
            tokenizationSpecification: {
              type: 'PAYMENT_GATEWAY',
              parameters: {
                gateway: 'example',
                gatewayMerchantId: 'exampleMerchantId',
              },
            },
          },
        ],
        merchantInfo: {
          merchantId: '12345678901234567890',
          merchantName: 'PizzaHub',
        },
        transactionInfo: {
          totalPriceStatus: 'FINAL',
          totalPriceLabel: 'Total',
          totalPrice: finalTotal.toFixed(2),
          currencyCode: 'INR',
          countryCode: 'IN',
        },
      };

      const paymentResult = await paymentsClient.loadPaymentData(
        paymentDataRequest
      );

      // Create order first
      const orderResponse = await apiCalls.createOrder({
        ...orderData,
        items: cart,
        totalPrice: finalTotal,
        paymentMethod: 'googlepay',
      });

      const newOrderId = orderResponse.data.order._id;

      // Process payment with Google Pay token
      const paymentResponse = await apiCalls.processPayment({
        orderId: newOrderId,
        amount: finalTotal,
        paymentMethod: 'googlepay',
        token: paymentResult.paymentMethodData.tokenizationData.token,
      });

      if (!paymentResponse.data.success) {
        setError(paymentResponse.data.message || 'Payment failed');
        setLoading(false);
        return;
      }

      clearCart();
      setShowSuccess(true);

      setTimeout(() => {
        navigate('/orders');
      }, 3000);
    } catch (err) {
      if (err.statusCode === 'CANCELED') {
        setError('Payment cancelled');
      } else {
        setError(err.message || 'Failed to process payment');
      }
    }
    setLoading(false);
  };

  if (showSuccess) {
    return (
      <div className="payment-page">
        <div className="success-message">
          <div className="success-icon">‚úì</div>
          <h2>Payment Successful!</h2>
          <p>Your order has been placed successfully.</p>
          <p>Redirecting to orders...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="payment-page">
      <h1>Google Pay Payment</h1>
      <div className="payment-container">
        <div className="payment-form-section">
          <h2>Choose Payment Method</h2>
          {error && <p className="error">{error}</p>}

          <div className="payment-methods-tabs">
            <button
              className={`method-tab ${paymentMethod === 'qr' ? 'active' : ''}`}
              onClick={generateQRCode}
              disabled={loading}
            >
              üì± Scan QR Code
            </button>
            <button
              className={`method-tab ${paymentMethod === 'direct' ? 'active' : ''}`}
              onClick={() => setPaymentMethod('direct')}
            >
              üí≥ Direct Payment
            </button>
          </div>

          {paymentMethod === 'qr' && (
            <div className="qr-payment-section">
              {!orderId ? (
                <div className="qr-setup">
                  <h3>Scan with Google Pay</h3>
                  <p>Click below to generate QR code for payment</p>
                  <button
                    onClick={generateQRCode}
                    disabled={loading}
                    className="qr-generate-btn"
                  >
                    {loading ? 'Generating...' : 'Generate QR Code'}
                  </button>
                </div>
              ) : (
                <div className="qr-display">
                  <h3>Scan to Pay</h3>
                  <div className="qr-code-container" ref={qrRef}></div>
                  <p className="qr-info">
                    Scan this QR code with Google Pay to complete payment
                  </p>
                  <p className="amount-display">
                    Amount to Pay: <strong>‚Çπ{finalTotal.toFixed(2)}</strong>
                  </p>
                  <button
                    onClick={handleQRScanned}
                    disabled={loading}
                    className="confirm-payment-btn"
                  >
                    {loading ? 'Processing...' : 'Confirm Payment'}
                  </button>
                  <button
                    onClick={() => setOrderId(null)}
                    className="cancel-qr-btn"
                  >
                    Use Different Method
                  </button>
                </div>
              )}
            </div>
          )}

          {paymentMethod === 'direct' && (
            <div className="direct-payment-section">
              <h3>Direct Google Pay Payment</h3>
              <div className="google-pay-info">
                <div className="info-box">
                  <h4>üîí Secure</h4>
                  <p>Direct payment through Google Pay</p>
                </div>
                <div className="info-box">
                  <h4>‚ö° Fast</h4>
                  <p>One-click checkout</p>
                </div>
                <div className="info-box">
                  <h4>üõ°Ô∏è Protected</h4>
                  <p>Your data is encrypted</p>
                </div>
              </div>
              <button
                onClick={handleDirectGooglePay}
                disabled={loading}
                className="google-pay-button"
              >
                {loading ? (
                  <>
                    <span className="spinner"></span>
                    Processing...
                  </>
                ) : (
                  <>
                    üí≥ Pay ‚Çπ{finalTotal.toFixed(2)} with Google Pay
                  </>
                )}
              </button>
            </div>
          )}
        </div>

        <div className="order-summary-section">
          <h2>Order Summary</h2>
          <div className="summary-items">
            {cart.map((item) => (
              <div key={item._id} className="summary-item">
                <span>
                  {item.name} x {item.quantity}
                </span>
                <span>‚Çπ{(item.price * item.quantity).toFixed(2)}</span>
              </div>
            ))}
          </div>
          <div className="summary-totals">
            <div className="summary-row">
              <span>Subtotal:</span>
              <span>‚Çπ{totalAmount.toFixed(2)}</span>
            </div>
            <div className="summary-row">
              <span>Tax (5%):</span>
              <span>‚Çπ{tax.toFixed(2)}</span>
            </div>
            <div className="summary-row total">
              <span>Total:</span>
              <span>‚Çπ{finalTotal.toFixed(2)}</span>
            </div>
          </div>
          <div className="delivery-info">
            <h4>Delivery Details</h4>
            <p>
              <strong>Address:</strong> {orderData.address}
            </p>
            <p>
              <strong>Phone:</strong> {orderData.phone}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
